import React, { useState } from 'react';
import './Main-Top.css';


function MainTop() {
    const [pageviews, setPageviews] = useState('$16');
    const [amountMonth, setAmountMonth] = useState('100K');

    function AddPrice(){
        let get = document.getElementById("inputline").value;
        if (get === '25'){
            setPageviews('$8');
            setAmountMonth('10K');
        }else if (get === '40'){
            setPageviews('$12');
            setAmountMonth('50K');
        }else if(get === '60') {
            setPageviews('$16');
            setAmountMonth('100K');
        }else if( get === '80' ){
            setPageviews('$24');
            setAmountMonth('500K');
        }else if(get === '100') {
            setPageviews('$36');
            setAmountMonth('1m');
        }

    }



    return(
        <div className="section">
            <div className="section__head">
                <p>{pageviews} Pageviews</p>
            </div>
            <form className="section__input">
                <input type="range"  min="20" max="100"   id="inputline"  onChange={AddPrice}/>
            </form>
            <div className="section__monthly">
                <p><span className="bold">$ {amountMonth}</span>/month</p>
            </div>
            <div className="section__pref">
                <p>Monthly Billing</p>
                <input type="radio"></input>
                <div className="yearly">
                    <p className="yearly__text">Yearly Billing</p>
                    <p className="yearly__discount">25%</p>
                </div>
            </div>
        </div>
    )
};


export default MainTop;
