import React, { useState } from 'react';
import './Main-Top.css';


function MainTop() {

    function AddPrice(){
        let get = document.getElementById("inputline").value;
        let amount;
        let full;
        if (get === 1){
            amount = '$8';
            full = '10K';
        }else if (get === 2){
            amount = '$12';
            full = '50K'
        }else if(get === 3) {
            amount = '$16';
            full = '100K'
        }else if( get === 4 ){
            amount = '$24';
            full = '500K'
        }else if(get === 5) {
            amount = '$36';
            full='1m'
        }
        console.log([get, amount, full]);
    }


    return(
        <div className="section">
            <div className="section__head">
                <p>{} Pageviews</p>
            </div>
            <form className="section__input">
                <input type="range" list="tickmarks" step="1" min="1" max="5" id="inputline" onChange={AddPrice}/>
                <svg xmlns="http://www.w3.org/2000/svg" width="22" height="13" className="polzunok" >
                    <g fill="#80FFF3" fill-rule="evenodd">
                        <path d="M16 2.558v7.884a1 1 0 001.735.679l3.639-3.943a1 1 0 000-1.356l-3.64-3.943A1 1 0 0016 2.558zM6 2.558v7.884a1 1 0 01-1.735.679L.626 7.178a1 1 0 010-1.356l3.64-3.943A1 1 0 016 2.558z"/>
                    </g>
                </svg>
            </form>
            <div className="section__monthly">
                <p><span className="bold">$ </span>/month</p>
            </div>
            <div className="section__pref">
                <p>Monthly Billing</p>
                <input type="radio"></input>
                <div className="yearly">
                    <p className="yearly__text">Yearly Billing</p>
                    <p className="yearly__discount">25%</p>
                </div>
            </div>
        </div>
    )
};


export default MainTop;
